import React from 'react'
import notes from '../assets/data'
import {useParams} from 'react-router-dom';

const NotePage = () => {
    let {id} = useParams();
    let note = notes.find(note => note.id === Number(id))
    return (
        <div className='note'>
            <div>
            <p>{ note?.body }</p>
            </div>
            
        </div>
    )
}

export default NotePage;
